import React, { Component } from 'react';
// import PlacesAutocomplete, {
//   geocodeByAddress,
//   getLatLng,
// } from 'react-places-autocomplete';
import Autocomplete from 'react-google-autocomplete';
import { MainConsumer } from "../../contexts/ContextMain";
import './InputPage.css';

let isError = false;
class InputPage extends Component {

  render() {
    return (
      <MainConsumer>
        {
          ({ handleOnChangeInputDestination, handleGetQuote }) => (
            <div className="main-delivery-input">
              <form>
                <Autocomplete
                  style={{ width: '80%' }}
                  onPlaceSelected={(place) => {
                    console.log(place, "place");

                    handleOnChangeInputDestination(place);
                  }}
                  types={['(regions)']}
                  componentRestrictions={{ country: "us" }}
                />
                {/* <input onChange={e => (handleOnChangeInputDestination(e.target.value))} className={`input-border green`} type="text" placeholder="Enter new destination in US" /> */}
              </form>
              <div className="text-danger">{isError ? 'This field cannot be blank' : ''}</div>
            </div>
          )
        }
      </MainConsumer>
    );
  }
}

export default InputPage;
